select a.Prop_1 as MBR_Scope, b.Prop_1 as MBR_Month,  CONCAT('P&L FI_', bu_sequence ) AS SheetName, bu_sequence AS BU_Code, bu_name as BU_Name,
CONCAT('R_P&L_FI_', a.Prop_1, '_',bu_sequence) AS RawTableName,  CONCAT('U_P&L_FI_', a.Prop_1, '_', bu_sequence) AS UnpvtTableName, currency_code as Currency_Code,
CONCAT('R_KPI_Pyramid_', a.Prop_1) AS KPIPyramidTableName
from ods.R_MBR_Currency_@{pipeline().parameters.pMBRScope},
(select Prop_1 from ods.R_MBR_Parameters_@{pipeline().parameters.pMBRScope}
where Prop_0 = 'MBR SCOPE') a,
(select Prop_1 from ods.R_MBR_Parameters_@{pipeline().parameters.pMBRScope}
where Prop_0 = 'MONTH') b
where bu_select is not null
